// declare hobby var and list in an array []
var hobbies = ['skating', 'cycling', '175mph+'];

// use push function to add to hobby var
hobbies.push('movies');
//log to console 
console.log(hobbies);

// use unshift to add a value to the beginning
hobbies.unshift('walking');

//log to console
console.log(hobbies);


/*
Use the pop method to remove a value from the back of the array
    array.pop()

Use the shift method to remove a value from the beginning of the array
    array.shift()

 TRY IT OUT AS MANY TIMES AS YOU LIKE!!

*/

var hobbies = ['skating', 'cycling', '175mph+'];

